
import { faker } from "@faker-js/faker";
export default (user,count) => {
    let data = [];
    for (let i = 0; i < count; i++) {
        const fake = {
taskID: faker.lorem.sentence(""),
userID: faker.lorem.sentence(""),
hoursSpent: faker.lorem.sentence(""),
logDate: faker.lorem.sentence(""),
description: faker.lorem.sentence(""),

updatedBy: user._id,
createdBy: user._id
        };
        data = [...data, fake];
    }
    return data;
};
